import { Transaction } from "./types";
export interface IAbstractSigner {
    signMessage(message: string): Promise<string>;
    signRawMessage(message: any): Promise<string>;
    signTypedData(domain: any, types: any, value: any, primaryType?: string): Promise<string>;
    getAddress(): Promise<string>;
    estimateGas(tx: Transaction): Promise<bigint>;
    signTransaction(tx: Transaction): Promise<string>;
    sendTransaction(tx: Transaction): Promise<string>;
    waitTillMined(txHash: string): Promise<any>;
}
